import React, {Component} from 'react';

import Aux from '../../hoc/Aux/Aux';
import UserInput from "../../components/UserInput/UserInput";
import Cards from "../../components/Cards/Cards";

class UserHome extends Component {
    state = {
        userName: "happy",
        posts: [
            {
                topic: "apple1",
                content: "an apple a day keeps the doctor away",
                image: "https://images.unsplash.com/photo-1581721152963-b9d78a76da14?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80",
                alt: "apple",
                key: "apple1"
            },
            {
                topic: "apple2",
                content: "an apple a day keeps the doctor away",
                key: "apple2"
            },
            {
                topic: "apple1",
                content: "an apple a day keeps the doctor away",
                key: "apple3"
            },
            {
                topic: "apple2",
                content: "an apple a day keeps the doctor away",
                key: "apple4"
            },
            {
                topic: "apple1",
                content: "an apple a day keeps the doctor away",
                key: "apple5"
            },
            {
                topic: "apple2",
                content: "an apple a day keeps the doctor away",
                key: "apple6"
            }
        ]
    };

    userNameChangeHandler = (event) => {
        this.setState({userName:event.target.value});
    };

    render() {
        return(
            <Aux>
                <UserInput changed={this.userNameChangeHandler} currentName={this.state.userName}/>
                <Cards posts={this.state.posts}/>
            </Aux>
        )
    }
}

export default UserHome;
