import React from 'react';

import classes from './Card.module.css';

const Card = (props) => {
    const image = props.image ? (<img src={props.image}/>):null;
    return (
        <div className={classes.Card}>
            {image}
            <div>
                <h4>{props.topic}</h4>
                <p>{props.content}</p>
            </div>
        </div>
    )
}

export default Card;
