import React, {Component} from 'react';
import axios from "axios";

import Aux from '../../hoc/Aux/Aux';
import UserInput from "../../components/UserInput/UserInput";
import Cards from "../../components/Cards/Cards";
import img from "../../assets/image/snow.png";
import classes from "./UserHome.module.css"

class UserHome extends Component {
    state = {
        userName: "happy",
        posts: [
            {
                title: "apple1",
                selftext: "an apple a day keeps the doctor away",
                // image: img,
                // alt: "apple",
                key: "apple1"
            },

        ]
    };

    componentWillUnmount() {
        this.fetchData();
    }

    fetchData = () => {
        axios.get('https://www.reddit.com/r/recipes.json').then(response => {
            this.setState({posts:response.data.data.children.splice(10)})
            console.log(this.state.posts);
        });
    };

    userNameChangeHandler = (event) => {
        this.setState({userName:event.target.value});
    };

    render() {
        return(
            <Aux>
                <div className={classes.Input}>
                    <UserInput changed={this.userNameChangeHandler} currentName={this.state.userName}/>
                    <button onClick={this.fetchData}>Search</button>
                </div>
                <Cards posts={this.state.posts}/>
            </Aux>
        )
    }
}

export default UserHome;
